# -*- coding: utf-8 -*-

"""Test utils."""

#------------------------------------------------------------------------------
# Imports
#------------------------------------------------------------------------------

from ..utils import _diff


#------------------------------------------------------------------------------
# Tests
#------------------------------------------------------------------------------

def test_diff():
    s = 'abcdef ghijkl'
    assert _diff(s, s) == ''

    assert _diff(s, ' ' + s) == s
    assert _diff(s, s + ' ') == s
